document.body.onload=function(){const page=document.getElementsByClassName("content"),link=document.getElementsByClassName("nav-link"),nav=document.getElementById("navbar"),footer=document.getElementById("footer"),navToggle=document.getElementById("nav-toggle"),navDrop=document.getElementById("right-nav-link"),footerLink=document.getElementsByClassName("footer-link");for(let i=0;i<link.length;i+=1)link[i].addEventListener("click",()=>{if(page[i].classList.add("active"),"home"!==page[i].id){footer.classList.remove("sticky"),footer.classList.add("textShadow"),navToggle.style.color="white",navToggle.classList.add("textShadow"),nav.style.backgroundColor="black",navDrop.style.backgroundColor="black",document.body.style.background="url(../../assets/images/about/bg.png) no-repeat center center fixed",document.body.style.backgroundSize="cover";for(let k=0;k<link.length;k+=1)link[k].style.color="white",link[k].classList.add("textShadow"),footerLink[k].style.color="white"}else{footer.classList.add("sticky"),footer.classList.remove("textShadow"),nav.style.background="transparent",navDrop.style.background="transparent",document.body.style.background="transparent",navToggle.style.color="black",navToggle.classList.remove("textShadow");for(let k=0;k<link.length;k+=1)link[k].style.color="black",link[k].classList.remove("textShadow"),footerLink[k].style.color="black"}for(let j=0;j<page.length;j+=1)page[j].classList.contains("active")&&page[i]!==page[j]&&page[j].classList.remove("active")});navToggle.addEventListener("click",()=>{navDrop.classList.toggle("drop")});for(let k=0;k<link.length;k+=1)link[k].addEventListener("click",()=>{navDrop.classList.contains("drop")&&navToggle.click()});window.addEventListener("resize",()=>{window.matchMedia("(min-width: 1024px)").matches&&navDrop.removeAttribute("class")})};