document.body.onload=function(){const page=document.getElementsByClassName("content"),link=document.getElementsByClassName("nav-link"),footer=document.getElementById("footer"),navToggle=document.getElementById("nav-toggle"),navDrop=document.getElementById("right-nav-link");for(let i=0;i<link.length;i+=1)link[i].addEventListener("click",()=>{page[i].classList.add("active"),"home"!==page[i].id?footer.classList.remove("sticky"):footer.classList.add("sticky");for(let j=0;j<page.length;j+=1)page[j].classList.contains("active")&&page[i]!==page[j]&&page[j].classList.remove("active")});navToggle.addEventListener("click",()=>{navDrop.classList.toggle("drop")});for(let k=0;k<link.length;k+=1)link[k].addEventListener("click",()=>{navDrop.classList.contains("drop")&&navToggle.click()});window.addEventListener("resize",()=>{window.matchMedia("(min-width: 1024px)").matches&&navDrop.removeAttribute("class")})};